sys_script_fix.script:
var gr = new GlideRecord('x_10559_neko_cat');
gr.query();

while (gr.next()) {
	var current = new GlideRecord('x_10559_neko_picture');
	current.addQuery('cat', gr.getValue('sys_id'));
	current.query();

	gr.collected_picture = false;
	gr.collected_butthole_picture = false;
	gr.collected_loaf_picture = false;

	while (current.next()) {
		gr.collected_picture = true;
		if (current.pose.name == 'Butthole') gr.collected_butthole_picture = true;
		if (current.pose.name == 'Loaf') gr.collected_loaf_picture = true;
	}

	if (gr.collected_picture.changes() || gr.collected_butthole_picture.changes() || gr.collected_loaf_picture.changes()) {
		gr.update();
	}
}
